                var validateForm = function ($form) {

                    var regExpNumber = /^[0-9]+$/, idRegEx = /^[0-9]{6}$/, regExpText = /^[A-Z][A-Za-z0-9_.\- ]+$/, regExpNotEmpty = /\S+/, isValid = true, index = 0;
