                        } else if ($field.is('[name="network_id"]')) {
                            if (!regExpNumber.test(value))
                            {
                                isValid = false;
                                index++;
                                createValidationMessage($field, 'Please enter a 5 digit number!', index);
                            }
                        } else if ($field.is('[type="text"]') && !regExpText.test(value)) {
                            if (!$field.is('[name="network_uuid"]')) {
                                isValid = false;
                                index++;
                                createValidationMessage($field, 'First letter should be capital', index);
                            }
                        } else if ($field.is('[type="number"]') && !regExpNumber.test(value)) {
                            isValid = false;
                            index++;
                            createValidationMessage($field, 'Please enter number!', index);
                        } else {
                            index++;
                        }
                    }
                    return isValid;
                };
                var createValidationMessage = function ($field, msg, index) {
                    //alert(index);
                    if (index === 1)
                    {
                        $field.focus();
                    }

                    $field.after('<span class="custom-validate-box">' + msg + '</span>');
                    //$field.siblings('.custom-validate-box').text(msg);
                    $(".required").click(function (e) {
                        if ($(e.target).is('.custom-validate-box') || ($(e.target).closest('.custom-validate-box').length === 0)) {
                            $field.next('.custom-validate-box').remove();
                        }
                    });

